}	
struct pos {
	LL max_x = 0, max_y = 0, min_x = INF, min_y = INF;
	pos operator+=(pos p) {
		this->max_x = max(this->max_x, p.max_x);
		this->min_x = min(this->min_x, p.min_x);
		this->max_y = max(this->max_y, p.max_y);
		this->min_y = min(this->min_y, p.min_y);
		return *this;
	}
	pos operator+(pos &p) {
		pos th = *this;
		th.max_x = max(this->max_x, p.max_x);
		th.min_x = min(this->min_x, p.min_x);
		th.max_y = max(this->max_y, p.max_y);
		th.min_y = min(this->min_y, p.min_y);
		return th;
	}
	pos operator+=(Pll& p) {
		this->max_x = max(this->max_x, p.first);
		this->min_x = min(this->min_x, p.first);
		this->max_y = max(this->max_y, p.second);
		this->min_y = min(this->min_y, p.second);
		return *this;
	}
};
pos pos_plus(pos p1, pos p2) {
	pos th;
	th.max_x = max(p1.max_x, p2.max_x);
	th.min_x = min(p1.min_x, p2.min_x);
	th.max_y = max(p1.max_y, p2.max_y);
	th.min_y = min(p1.min_y, p2.min_y);
	return th;
}
struct Seg {
	LL n;

	vector<pos>node;
	Seg(LL n_) {
		n = 1; while (n < n_)n *= 2;
		node.resize(n * 2 - 1);
	}
	void update(LL i, Pll p) {
		i = i + n - 1;
		node[i] += p;
		while (i > 0) {
			i = (i - 1) / 2;
			node[i] += node[i * 2 + 1];
			node[i] += node[i * 2 + 2];
		}
	}
	pos find(LL i, LL a, LL b, LL l, LL r) {
		if (b < l || r < a)return pos();
		if (l <= a && b <= r)return node[i];
		return pos_plus(find(i * 2 + 1, a, (a + b) / 2, l, r), find(i * 2 + 2, (a + b + 1) / 2, b, l, r));
	}
};

