using namespace std;

typedef long long ll;
typedef pair<int, int> P;
typedef pair<int, P> Q;
typedef pair<P, P> R;

const int inf = (int)2e6;

int n, d;
Q z[200000];
R st[(2 << 19) - 1];

R st_init(int k, int l, int r){
	if(r - l == 1){
		return st[k] = R(z[l].second, z[l].second);
	}
	R p = st_init(k * 2 + 1, l, (l + r) / 2);
	R q = st_init(k * 2 + 2, (l + r) / 2, r);
	return st[k] = R(
		P(min(p.first.first, q.first.first), min(p.first.second, q.first.second)),
		P(max(p.second.first, q.second.first), max(p.second.second, q.second.second))
	);
}

R st_minmax(int a, int b, int k, int l, int r){
	if(b <= l || r <= a){
		return R(
			P(inf, inf),
			P(0, 0)
		);
	}
	if(a <= l && r <= b){
		return st[k];
	}
	R p = st_minmax(a, b, k * 2 + 1, l, (l + r) / 2);
	R q = st_minmax(a, b, k * 2 + 2, (l + r) / 2, r);
	return R(
		P(min(p.first.first, q.first.first), min(p.first.second, q.first.second)),
		P(max(p.second.first, q.second.first), max(p.second.second, q.second.second))
	);
}

