	scanf("%d%d", &n, &d);
	rep(i, n){
		int x, y, b;
		scanf("%d%d%d", &x, &y, &b);
		z[i] = Q(b, P(x, y));
	}

	sort(z, z + n);
	st_init(0, 0, n);

	ll ans = 0;
	int s = 0, t = 0;
	while(t < n){
		while(t < n && z[t].first <= z[s].first + d){
			++t;
		}
		R x = st_minmax(s, t, 0, 0, n);
		ans = max(ans, ll(x.second.first - x.first.first) * ll(x.second.second - x.first.second));
		++s;
	}
	printf("%lld\n", ans);
	return 0;
}

