typedef pair <int,pair <int,int> > P;
typedef long long ll;
int n,d,tree[4][(1<<19)];
P star[200001];

void update(int i,int flg,int x){
  i = i+(1<<18)-1;
  tree[flg][i] = x;
  while(i) {
    i = (i-1)/2;
    tree[flg][i] = max(tree[flg][i*2+1],tree[flg][i*2+2]);
  }
}

int find(int a,int b,int ima,int L,int R,int flg){
  if(R<=a || b<=L) return -INF;
  if(a<=L && R<=b) return tree[flg][ima];
  int M = (L+R)/2;
  return max(find(a,b,ima*2+1,L,M,flg),find(a,b,ima*2+2,M,R,flg));
}

int main() {
