  for(int i=0;i<4;i++) 
    for(int j=0;j<(1<<18);j++) tree[i][j] = -INF;

  for(int i=0;i<n;i++) {
    update(i,0,star[i].s.f); // max(x);
    update(i,1,star[i].s.s); // max(y);
    update(i,2,-star[i].s.f); // min(x);
    update(i,3,-star[i].s.s); // min(y);
  }

  long long S=0;
  int ima=0;
  for(int i=0;i<n;i++){
