	// segment tree initial
	int m=1;
	while(m<n)m*=2;
	xms.init(m);
	xMs.init(m);
	yms.init(m);
	yMs.init(m);
	//

	REP(i, n){
		int x, y, b;
		cin >> x >> y >> b;
		stars[i] = MP(b, MP(x, y));
	}
	sort(stars, stars + n, comp);
	REP(i, n){
		int b = stars[i].first, x = stars[i].second.first, y = stars[i].second.second;
		if(b!=last){
			if(si > -1)calc();
			si++;
			last = b;
		}
		xm = min(xm, x);
		xM = max(xM, x);
		ym = min(ym, y);
		yM = max(yM, y);
	}
