	int *Min, *Max;
	int size;
	Seg(int n,int *X) {
		size = 1;
		while (n > size) size *= 2;
		Min = (int*)malloc(sizeof(int)*(2 * size - 1));
		Max = (int*)malloc(sizeof(int)*(2 * size - 1));
		fill(Min, Min + (2 * size - 1), INF);
		fill(Max, Max + (2 * size - 1), 0);
		for (int i = 0; i < n; i++) {
			Min[i+(size-1)] = Max[i+(size-1)] = X[i];
		}
		for (int i = size - 2; i >= 0; i--) {
			Min[i] = min(Min[i * 2 + 1], Min[i * 2 + 2]);
			Max[i] = max(Max[i * 2 + 1], Max[i * 2 + 2]);
		}
	}
	int max_q(int a, int b, int k, int l, int r) {
		if (a <= l&&r <= b) {
			return Max[k];
		}
		else if (r <= a || b <= l) {
			return 0;
		}
		return max(max_q(a, b, k * 2 + 1, l, (l + r) / 2), max_q(a, b, k * 2 + 2, (l + r) / 2, r));
	}
	int min_q(int a, int b, int k, int l, int r) {
		if (a <= l&&r <= b) {
			return Min[k];
		}
		else if (r <= a || b <= l) {
			return INF;
		}
		return min(min_q(a, b, k * 2 + 1, l, (l + r) / 2), min_q(a, b, k * 2 + 2, (l + r) / 2, r));
	}
};
int main() {
	int N, d;
	scanf("%d %d", &N, &d);
	vector<P2> star;
	for (int i = 0; i < N; i++) {
		int x, y, b;
		scanf("%d %d %d", &x, &y, &b);
		star.push_back(P2(b, P1(x, y)));
	}
	sort(star.begin(), star.end());
	for (int i = 0; i < N; i++) {
		B[i] = star[i].first;
		X[i] = star[i].second.first;
		Y[i] = star[i].second.second;
	}
	Seg segX(N, X), segY(N, Y);
	long long ans = 0;
	for (int i = 0; i < N; i++) {
		int pos = upper_bound(B, B + N, B[i] + d) - B;
		int minX = segX.min_q(i, pos, 0, 0, segX.size);
		int maxX = segX.max_q(i, pos, 0, 0, segX.size);
		int minY = segY.min_q(i, pos, 0, 0, segY.size);
		int maxY = segY.max_q(i, pos, 0, 0, segY.size);
		ans = max(ans, (long long)(maxX - minX)*(maxY - minY));
	}
	printf("%lld\n", ans);
}

