struct SegTree {
    int n;
    vector<int> min_dat;
    vector<int> max_dat;
    SegTree(int N) {
        n=1;
        while(n<N) n*=2;
        min_dat.assign(1<<19,INF);
        max_dat.assign(1<<19,0);
    }

    void update(int k,int a) {
        k+=n-1;
        min_dat[k]=a;
        max_dat[k]=a;
        while(k>0) {
            k=(k-1)/2;
            min_dat[k]=min(min_dat[k*2+1],min_dat[k*2+2]);
            max_dat[k]=max(max_dat[k*2+1],max_dat[k*2+2]);
        }
    }

    int min_query(int a,int b,int k,int l,int r) {
        //printf("%d %d %d %d %d\n",a,b,k,l,r);
        if(r<=a||b<=l) return INF;

        if(a<=l&&r<=b) return min_dat[k];
        else {
            int vl=min_query(a,b,k*2+1,l,(l+r)/2);
            int vr=min_query(a,b,k*2+2,(l+r)/2,r);
            return min(vl,vr);
        }
    }

    int max_query(int a,int b,int k,int l,int r) {
        //printf("%d %d %d %d %d\n",a,b,k,l,r);
        if(r<=a||b<=l) return 0;

        if(a<=l&&r<=b) return max_dat[k];
        else {
            int vl=max_query(a,b,k*2+1,l,(l+r)/2);
            int vr=max_query(a,b,k*2+2,(l+r)/2,r);
            return max(vl,vr);
        }
    }

    void dump() {
        rep(i,n) cout<<max_dat[i]<<" ";
        cout<<endl;
    }

};
