	LL getmin(int a, int b, int k = 0, int l = 0, int r = -1) {
		if (r < 0) r = n;
		if (r <= a || b <= l) return HINF<LL>();
		if (a <= l && r <= b) return node[k];

		LL vl = getmin(a, b, 2 * k + 1, l, (l + r) / 2);
