long min(long a, long b)
{
    return a < b ? a : b;
}

long max(long a, long b)
{
    return a > b ? a : b;
}

struct Star
{
    long x, y, b;
public:
    Star(long x, long y, long b): x(x), y(y), b(b) {}
    bool operator<(const Star& s)
    {
        return b < s.b;
    }
};

struct Range
{
    long x1, y1, x2, y2, b;
public:
    Range(const Star& s): x1(s.x), y1(s.y), x2(s.x), y2(s.y), b(s.b) {}
    void add(const Star& s)
    {
        x1 = min(x1, s.x);
        y1 = min(y1, s.y);
        x2 = max(x2, s.x);
        y2 = max(y2, s.y);
    }
    void add(const Range& r)
    {
        x1 = min(x1, r.x1);
        y1 = min(y1, r.y1);
        x2 = max(x2, r.x2);
        y2 = max(y2, r.y2);
    }
    long area()
    {
        return (x2 - x1) * (y2 - y1);
    }
};

int main(int argc, const char *argv[])
{
    long N, d;
    scanf("%ld %ld", &N, &d);

    std::vector<Star> stars;
    stars.reserve(200000);
    for (long i = 0; i < N; i++)
    {
        long x, y, b;
        scanf("%ld %ld %ld", &x, &y, &b);
        stars.push_back(Star(x, y, b));
    }
    std::sort(stars.begin(), stars.end());

    std::vector<Range> ranges;
    ranges.push_back(Range(stars[0]));
    for (long i = 1; i < N; i++)
    {
        int cur_b = ranges.back().b;
        if (cur_b == stars[i].b)
        {
            ranges.back().add(stars[i]);
        }
        else
        {
            ranges.push_back(Range(stars[i]));
        }
    }

    long max_area = 0;

    for (long i = 0; i < ranges.size(); i++)
    {
        Range range = ranges[i];

        for (long j = i; ranges[j].b - ranges[i].b <= d && j < ranges.size(); j++)
        {
            range.add(ranges[j]);
        }

        max_area = max(range.area(), max_area);
    }

    printf("%ld\n", max_area);

    return 0;
}

