typedef long long ll;

struct MinSegtree
{
  int n;
  ll dat[2*MAX_N-1];
  MinSegtree(int _n)
  {
    n = 1;
    while(n < _n)
      n *= 2;
    for(int i=0;i<n*21;i++)
      dat[i] = INF;
  }

  void update(int k, int a)
  {
    k += n - 1;
    dat[k] = a;
    while(k > 0){
      k = (k - 1) / 2;
      dat[k] = min(dat[k * 2 + 1], dat[k * 2 + 2]);
    }
  }

  ll query(int a, int b, int k, int l, int r)
  {
    if(r <= a || b <= l) return INF;

    if(a <= l && r <= b) return dat[k];
    else{
      ll vl = query(a, b, k*2+1, l, (l + r) / 2);
      ll vr = query(a, b, k*2+2, (l + r) / 2, r);
      return min(vl, vr);
    }
  }

  int size()
  {
    return n;
  }
};

struct MaxSegtree
{
  int n;
  ll dat[2*MAX_N-1];
  MaxSegtree(int _n){
    n = 1;
    while(n < _n)
      n *= 2;
    for(int i=0;i<n*2-1;i++)
      dat[i] = -1;
  }

  void update(int k, int a)
  {
    k += n - 1;
    dat[k] = a;
    while(k > 0){
      k = (k - 1) / 2;
      dat[k] = max(dat[k * 2 + 1], dat[k * 2 + 2]);
    }
  }

  ll query(int a, int b, int k, int l, int r)
  {
    if(r <= a || b <= l) return -1;

    if(a <= l && r <=b ) return dat[k];
    else{
      ll vl = query(a, b, k * 2 + 1, l, (l + r) / 2);
      ll vr = query(a, b, k * 2 + 2, (l + r) / 2, r);
      return max(vl, vr);
    }
  }

  int size()
  {
    return n;
  }
};

