const long long INF = 5e18;

struct RMinQ {
private:
	int n;
	vector< long long >node;
public:
	RMinQ(vector< long long >v) {
		int sz = v.size();
		n = 1;
		while (n < sz)n *= 2;
		node.resize(2 * n - 1, INF);
		for (int i = 0;i < sz;i++)node[i + n - 1] = v[i];
		for (int i = n - 2;i >= 0;i--)node[i] = min(node[2 * i + 1], node[2 * i + 2]);
	}
	long long getmin(int a, int b, int k = 0, int l = 0, int r = -1) {
		if (r < 0)r = n;
		if (r <= a || b <= l)return INF;
		if (a <= l && r <= b)return node[k];
		long long vl = getmin(a, b, 2 * k + 1, l, (l + r) / 2);
		long long vr = getmin(a, b, 2 * k + 2, (l + r) / 2, r);
		return min(vl, vr);
	}
};

struct RMaxQ {
private:
	int n;
	vector< long long >Node;
public:
	RMaxQ(vector< long long >v) {
		int sz = v.size();
		n = 1;
		while (n < sz)n *= 2;
		Node.resize(2 * n - 1, -1);
		for (int i = 0;i < sz;i++)Node[i + n - 1] = v[i];
		for (int i = n - 2;i >= 0;i--)Node[i] = max(Node[2 * i + 1], Node[2 * i + 2]);
	}
	long long getmax(int a, int b, int k = 0, int l = 0, int r = -1) {
		if (r < 0)r = n;
		if (r <= a || b <= l)return -1;
		if (a <= l && r <= b)return Node[k];
		long long vl = getmax(a, b, 2 * k + 1, l, (l + r) / 2);
		long long vr = getmax(a, b, 2 * k + 2, (l + r) / 2, r);
		return max(vl, vr);
	}
};

