struct SEG {
	vector<PII> seg;
	int aryN;
	int segN;

	int inToN(int i){
		return aryN+i-1;
	}
	int p(int i){
		return (i-1)/2;
	}
	void update(int n){
		if(n == 0) return;
		seg[p(n)].first = max(seg[p(n)].first, seg[n].first);
		seg[p(n)].second = min(seg[p(n)].second, seg[n].second);
		update(p(n));
	}
	void init(int n){
		aryN = (int)pow(2, (int)ceil(log2(n)));
		segN = 2*aryN-1;
		seg = vector<PII>(segN);
	}
	void initSEG(){
		rep(i, 0, aryN) seg[i] = { 0, (int)INF };
		rep(i, 0, aryN) update(inToN(i));
	}
	int getMax(int a, int b, int k = 0, int l = 0, int r = -1){
		if(r < 0) r = aryN;
		if(r <= a || b <= l) return -1;
		if(a <= l && r <= b) return seg[k].first;
		int vl = getMax(a, b, 2*k+1, l, (l+r)/2);
		int vr = getMax(a, b, 2*k+2, (l+r)/2, r);
		return max(vl, vr);
	}
	int getMin(int a, int b, int k = 0, int l = 0, int r = -1){
		if(r < 0) r = aryN;
		if(r <= a || b <= l) return INF;
		if(a <= l && r <= b) return seg[k].second;
		int vl = getMin(a, b, 2*k+1, l, (l+r)/2);
		int vr = getMin(a, b, 2*k+2, (l+r)/2, r);
		return min(vl, vr);
	}
};


int N, d;
TIII star[200010];
SEG X, Y;
int br[200010];


void makeXY(){
	X.init(N), Y.init(N);
	rep(i, 0, N) X.seg[X.inToN(i)] = { get<1>(star[i]), get<1>(star[i]) }, Y.seg[Y.inToN(i)] = { get<2>(star[i]), get<2>(star[i]) };
	rep(i, N, X.aryN) X.seg[X.inToN(i)] = Y.seg[Y.inToN(i)] = { 0, (int)INF };
	X.initSEG(), Y.initSEG();
}


int solve(){
	int ans = 0;
	rep(i, 0, N) br[i] = get<0>(star[i]);
	rep(i, 0, N){
		int p = upper_bound(RNG(br, N), br[i]+d)-&br[0];
		int x1 = X.getMax(i, p), x2 = X.getMin(i, p);
		int y1 = Y.getMax(i, p), y2 = Y.getMin(i, p);
		ans = max(ans, (x1-x2)*(y1-y2));
	}
	return ans;
}


