struct Seg {
    int n;
    int dat[MAX_N * 2];

    void Init(int pn, int def) {
        n = 1;
        while (n < pn) n *= 2;
        fill(dat, dat + MAX_N * 2, def);
    }
};

struct SegMin : Seg {
    int Min(int s, int t) {
        return Min(s, t, 0, 0, n);
    }

    int Min(int s, int t, int k, int l, int r) {
        if (r <= s || t <= l) return INF;
        if (s <= l && r <= t) return dat[k];

        int t1 = Min(s, t, k * 2 + 1, l, (l + r) / 2);
        int t2 = Min(s, t, k * 2 + 2, (l + r) / 2, r);
        return min(t1, t2);
    }

    void Update(int k, int x) {
        k += n - 1;
        dat[k] = x;
        while (0 < k) {
            k = (k - 1) / 2;
            dat[k] = min(dat[k * 2 + 1], dat[k * 2 + 2]);
        }
    }
};

struct SegMax : Seg {
    int Max(int s, int t) {
        return Max(s, t, 0, 0, n);
    }

    int Max(int s, int t, int k, int l, int r) {
        if (r <= s || t <= l) return 0;
        if (s <= l && r <= t) return dat[k];

        int t1 = Max(s, t, k * 2 + 1, l, (l + r) / 2);
        int t2 = Max(s, t, k * 2 + 2, (l + r) / 2, r);
        return max(t1, t2);
    }

    void Update(int k, int x) {
        k += n - 1;
        dat[k] = x;
        while(0 < k) {
            k = (k - 1) / 2;
            dat[k] = max(dat[k * 2 + 1], dat[k * 2 + 2]);
        }
    }
};

