	// segment tree initial
	int m=1;
	while(m<n)m*=2;
	xms.init(m);
	xMs.init(m);
	yms.init(m);
	yMs.init(m);
	//

	REP(i, n){
		int x, y, b;
		cin >> x >> y >> b;
		stars[i] = MP(b, MP(x, y));
	}
	sort(stars, stars + n, comp);
	REP(i, n){
		int b = stars[i].first, x = stars[i].second.first, y = stars[i].second.second;
		if(b!=last){
			si++;
			last = b;
			calc();
		}
		xm = min(xm, x);
		xM = max(xM, x);
		ym = min(ym, y);
		yM = max(yM, y);
	}
	si++;
	calc();

	si++; // to use as sizes' size
	int ans = 0;
	REP(i, n){
		int b = stars[i].first;
		int st = sizeRef[b];
		int ed = upper_bound(sizes, sizes + si, b + d) - sizes;
		xm = xms.query(st, ed);
		xM = -xMs.query(st, ed);
		ym = yms.query(st, ed);
		yM = -yMs.query(st, ed);
		ans = max(ans, (xM - xm) * (yM - ym));
	}
	cout << ans << endl;
}

