struct SegTree { // ??????min,max
	int n;
	vector<int> Max;
	vector<int> Min;

	void init(int n_)
	{
		n = 1;
		while (n < n_) n *= 2;

		Max.resize(2 * n);
		Min.resize(2 * n);

		for (int i = 0; i < 2 * n - 1; ++i) {
			Max[i] = -inf;
			Min[i] = inf;
		}
	}

	void update(int idx, int val)
	{
		idx += n - 1;
		Min[idx] = val;
		Max[idx] = val;

		while (idx > 0) {
			idx = (idx - 1) / 2;
			Min[idx] = min(Min[idx * 2 + 1], Min[idx * 2 + 2]);
			Max[idx] = max(Max[idx * 2 + 1], Max[idx * 2 + 2]);
		}
	}

	int get_min(int a, int b, int k = 0, int l = 0, int r = -1) // [a, b)???min
	{
		if (r == -1) r = n;
		if (r <= a || b <= l) return inf;
		if (a <= l && r <= b) return Min[k];

		int vl = get_min(a, b, k * 2 + 1, l, (l + r) / 2);
		int vr = get_min(a, b, k * 2 + 2, (l + r) / 2, r);

		return min(vl, vr);
	}

	int get_max(int a, int b, int k = 0, int l = 0, int r = -1) // [a, b)???max
	{
		if (r == -1) r = n;
		if (r <= a || b <= l) return -inf;
		if (a <= l && r <= b) return Max[k];

		int vl = get_max(a, b, k * 2 + 1, l, (l + r) / 2);
		int vr = get_max(a, b, k * 2 + 2, (l + r) / 2, r);

		return max(vl, vr);
	}
};

