  if( r <= a || b <= l ) return INT_MAX;
  if( a <= l && r <= b ) return dat[k];
  else {
    ll vl = query(a, b, k * 2 + 1, l, (l + r) / 2, dat);
    ll vr = query(a, b, k * 2 + 2, (l + r) / 2, r, dat);
    return min(vl, vr);
  }
}

