int leftMostBit(int x) {
  if(x == 0) return 0;
  x = (x & 0x55555555) << 1 | (x >> 1) & 0x55555555;
  x = (x & 0x33333333) << 2 | (x >> 2) & 0x33333333;
  x = (x & 0x0f0f0f0f) << 4 | (x >> 4) & 0x0f0f0f0f;
  x = (x << 24) | ((x & 0xff00) << 8) | ((x >> 8) & 0xff00) | (x >> 24);
  int deBruijnSequence[32] = {0, 1, 23, 2, 29, 24, 19, 3, 30, 27, 25, 11, 20, 8, 4, 13, 31, 22, 28, 18, 26, 10, 7, 12, 21, 17, 9, 6, 16, 5, 15, 14};
  return 31 - deBruijnSequence[((x & -x) * 0x076be629U) >> 27];
}
class RangeMinimumQuery {
public:
  RangeMinimumQuery(int s) {
    size_ = 1 << leftMostBit(s);
    if(size_ != s) size_ <<= 1;
    node_ = std::vector<long long int>((size_ << 1) - 1, INF);
  }
  long long int find(int left, int right) const {
    return query(left, right, 0, 0, size_ - 1);
  }
  void update(int index, int value) {
    int i = index + size_ - 1;
    node_[i] = value;
    while((i = parent(i)) != NIL) node_[i] = std::min(node_[left_child(i)], node_[right_child(i)]);
  }
private:
  int parent(int node) const {return node == 0 ? NIL : (node - 1) >> 1;}
  int left_child(int node) const {return (node << 1) + 1;}
  int right_child(int node) const {return (node << 1) + 2;}
  long long int query(int a, int b, int k, int l, int r) const {
    if(b < l || r < a) return INF;
    if(a <= l && r <= b) return node_[k];
    int vl = query(a, b, left_child(k), l, (l + r) / 2);
    int vr = query(a, b, right_child(k), (l + r) / 2 + 1, r);
    return std::min(vl, vr);
  }

  int size_;
  std::vector<long long int> node_;
};

#include<iostream>
using namespace std;

struct Star {long long int x, y, b;};
bool operator<(const Star& lhs, const Star& rhs) {return lhs.b < rhs.b;}

int main() {
  int N, d;
  cin >> N >> d;
  vector<Star> star(N);
  for(auto& s: star) cin >> s.x >> s.y >> s.b;
  sort(begin(star), end(star));
  RangeMinimumQuery left(N), right(N), top(N), bottom(N);
  for(int i = 0; i < N; ++i) {
    left.update(i, star[i].x);
    right.update(i, -star[i].x);
    top.update(i, star[i].y);
    bottom.update(i, -star[i].y);
  }
  long long int answer = 0;
  int low, high;
  for(low = high = 0; low < N; ++low) {
    while(high < N - 1 && star[high].b - star[low].b < d) ++high;
    long long int area = -right.find(low, high) - left.find(low, high);
    area *= -bottom.find(low, high) - top.find(low, high);
    answer = max(answer, abs(area));
  }
  cout << answer << endl;
}

