void makeXY(){
	X.init(N), Y.init(N);
	rep(i, 0, N) X.seg[X.inToN(i)] = { get<1>(star[i]), get<1>(star[i]) }, Y.seg[Y.inToN(i)] = { get<2>(star[i]), get<2>(star[i]) };
	rep(i, N, X.aryN) X.seg[X.inToN(i)] = Y.seg[Y.inToN(i)] = { -1, (int)INF };
	X.initSEG(), Y.initSEG();
}


