typedef long long ll;
typedef pair<ll,ll> P;
typedef pair<ll,P> P2;
struct min_segtree{
private:
  vector<ll> node;
  int siz;
public:
  min_segtree(int n){
    siz=1;
    while(siz<=n) siz*=2;
    node.resize(siz*2+1,(ll)2147483647);
  }
  void upd(int pos,int x){
    pos+=siz-1;
    node[pos]=x;
    while(pos>0){
      pos=(pos-1)/2;
      node[pos]=min(node[pos*2+1],node[pos*2+2]);
    }
  }
  ll get_min(int a,int b,int pos,int l,int r){
    if(a<=l&&r<=b) return node[pos];
    if(r<=a||b<=l) return 2147483647;
    int mid=(l+r)/2;
    ll lb=get_min(a,b,pos*2+1,l,mid);
    ll rb=get_min(a,b,pos*2+2,mid,r);
    return min(lb,rb);
  }
  ll min_q(int a,int b){
    return get_min(a,b,0,0,siz);
  }
};
struct max_segtree{
private:
  vector<ll> node;
  int siz;
public:
  max_segtree(int n){
    siz=1;
    while(siz<=n) siz*=2;
    node.resize(siz*2+1,0);
  }
  void upd(int pos,int x){
    pos+=siz-1;
    node[pos]=x;
    while(pos>0){
      pos=(pos-1)/2;
      node[pos]=max(node[pos*2+1],node[pos*2+2]);
    }
  }
  ll get_max(int a,int b,int pos,int l,int r){
    if(a<=l&&r<=b) return node[pos];
    if(r<=a||b<=l) return 0;
    int mid=(l+r)/2;
    ll lb=get_max(a,b,pos*2+1,l,mid);
    ll rb=get_max(a,b,pos*2+2,mid,r);
    return max(lb,rb);
  }
  ll max_q(int a,int b){
    return get_max(a,b,0,0,siz);
  }
};

int main(){
  ll n,d;
  cin>>n>>d;
  vector<P2> vec;
  vector<ll> bright;
  for(int i=0;i<n;i++){
    ll x,y,b;
    cin>>x>>y>>b;
    bright.push_back(b);
    vec.push_back(P2(b,P(x,y)));
  }
  sort(bright.begin(),bright.end());
  sort(vec.begin(),vec.end());
  min_segtree minsegx=min_segtree(n+5),minsegy=min_segtree(n+5);
  max_segtree maxsegx=max_segtree(n+5),maxsegy=max_segtree(n+5);

  for(int i=0;i<n;i++){
    ll x=vec[i].second.first,y=vec[i].second.second;
    minsegx.upd(i,x);
    maxsegx.upd(i,x);
    minsegy.upd(i,y);
    maxsegy.upd(i,y);
  }
  ll ans=0;
  for(int i=0;i<n;i++){
    ll jt=upper_bound(bright.begin(),bright.end(),bright[i]+d)-bright.begin();
    ll dx=maxsegx.max_q(i,jt)-minsegx.min_q(i,jt);
    ll dy=maxsegy.max_q(i,jt)-minsegy.min_q(i,jt);
    ans=max(ans,dx*dy);
  }
  cout<<ans<<endl;
  return 0;
}

