#include <bits/stdc++.h>
using namespace std;

typedef long long ll;
const int INF = 1 << 28;

struct Point
{
  int x, y;
  Point() {}
  Point(int x, int y) : x(x), y(y) {}
  void input() { scanf("%d %d", &x, &y); }
};

struct Maxminp
{
  Point maxv, minv;
  Maxminp() {}
  Maxminp(Point maxv, Point minv) : maxv(maxv), minv(minv) {}
  ll area() { return ((ll)(maxv.x - minv.x) * (ll)(maxv.y  - minv.y)); }
  void chmaxmin(Maxminp d)
  {
    maxv = Point(max(maxv.x, d.maxv.x), max(maxv.y, d.maxv.y)); 
    minv = Point(min(minv.x, d.minv.x), min(minv.y, d.minv.y));
  }
};

const Maxminp reset = Maxminp(Point(-INF, -INF), Point(INF, INF));

struct Segtree
{
  vector < Maxminp > data;
  int size;

  Segtree() {}

  Segtree(int sz)
  {
    int g = 1;
    while(g < sz) g *= 2;
    size = g;
    data.assign(size * 4, reset);
  }

  void update(Point val, int p)
  {
    int pos = p + size - 1;
    data[pos] = Maxminp(val, val);
    while(pos != 0) {
      pos = (pos - 1) >> 1;
      data[pos].chmaxmin(data[pos * 2 + 1]);
      data[pos].chmaxmin(data[pos * 2 + 2]);
    }
  }

  Maxminp query(int a, int b) { return (query(a, b, 0, 0, size - 1)); }
  Maxminp query(int a, int b, int k, int l, int r)
  {
    Maxminp ret = reset;
    if(r < a || b < l) return (ret);
    if(a <= l && r <= b) return (data[k]);
    ret.chmaxmin(query(a, b, k * 2 + 1, l, (l + r) / 2));
    ret.chmaxmin(query(a, b, k * 2 + 2, (l + r) / 2 + 1, r));
    return (ret);
  }
};

struct Star
{
  Point p;
  int b;
  Star() {}
  Star(Point p, int b) : p(p), b(b) {}
  void input() { p.input(); scanf("%d", &b); }
  bool operator < (const Star d) const { return (b < d.b); }
};

int main()
{
  int N, D;
  Segtree tree;
  Star data[200005];
  ll maxv = 0;
  int k = 0;

  cin >> N >> D;
  tree = Segtree(N);

  for(int i = 0; i < N; i++) data[i].input();
  sort(data, data + N);

  for(int i = 0; i < N; i++) {
    tree.update(data[i].p, i);
    while(k < i && data[k].b + D < data[i].b) k++;
    maxv = max(tree.query(k, i).area(), maxv);
  }

  cout << maxv << endl;

  return (0);
}

