		}
	}

	if(search_left <= node_left && search_right >= node_right){
		if(type == Left){
			return Tree[node_id].most_left;
		}else if(type == Right){
			return Tree[node_id].most_right;
		}else if(type == Top){
			return Tree[node_id].most_top;
		}else{
			return Tree[node_id].most_bottom;
		}
	}

	if(type == Left || type == Top){
		ll left_min = find(search_left,search_right,2*node_id+1,node_left,(node_left+node_right)/2,type);
		ll right_min = find(search_left,search_right,2*node_id+2,(node_left+node_right)/2+1,node_right,type);

		return min(left_min,right_min);
	}else{ //type == Right || type == Bottom
		ll left_max = find(search_left,search_right,2*node_id+1,node_left,(node_left+node_right)/2,type);
		ll right_max = find(search_left,search_right,2*node_id+2,(node_left+node_right)/2+1,node_right,type);

		return max(left_max,right_max);
	}
}


int main(){

	int first_N;
	ll Diff_Max;

	scanf("%d %lld",&first_N,&Diff_Max);

	init(first_N);

	info = (Info*)malloc(sizeof(Info)*(2*N));
	Tree = (RMQ*)malloc(sizeof(RMQ)*(2*N));

	for(int i = 0; i < 2*N-1; i++){
		Tree[i].set(BIG_NUM,-1,BIG_NUM,-1);
	}

	for(int i = 0; i < first_N; i++){
		scanf("%lld %lld %lld",&info[i].x,&info[i].y,&info[i].Brightness);
	}

	sort(info,info+first_N);

	for(int i = 0; i < first_N; i++){
		update(i,info[i]);
	}

	ll ans = 0;
	int left,right,m,pre_right = 0,loc;
	ll calc_left,calc_right,calc_top,calc_bottom;


	for(int start = 0; start < first_N; start++){

		left = pre_right,right = first_N-1;
		m = (left + right)/2;
		loc = -1;
		while(left <= right){
			if(info[m].Brightness-info[start].Brightness <= Diff_Max){
				loc = max(loc,m);
				left = m+1;
			}else{
				right = m-1;
			}
			m = (left+right)/2;
		}
		if(loc == -1)break;

		calc_left = find(start,loc,0,0,N-1,Left);
		calc_right = find(start,loc,0,0,N-1,Right);
		calc_top = find(start,loc,0,0,N-1,Top);
		calc_bottom = find(start,loc,0,0,N-1,Bottom);

		ans = max(ans,(calc_right-calc_left)*(calc_bottom-calc_top));

		pre_right = loc;
	}

	printf("%lld\n",ans);

	return 0;
}

