    SparseTable<ll> maxX(n, 0, [](ll l, ll r){return max(l, r);});
    SparseTable<ll> minY(n, 0, [](ll l, ll r){return min(l, r);});
    SparseTable<ll> maxY(n, 0, [](ll l, ll r){return max(l, r);});

