ll seg_mi[MAX_N * 2],seg_mx[MAX_N*2];
ll n, d;

struct RMQ
{
	void update(ll k, ll x)
	{
		k += n - 1;
		seg_mi[k] = x;
		seg_mx[k] = x;
		while (k)
		{
			k = (k - 1) / 2;
			seg_mi[k] = min(seg_mi[k * 2 + 1], seg_mi[k * 2 + 2]);
			seg_mx[k] = max(seg_mx[k * 2 + 1], seg_mx[k * 2 + 2]);
		}
	}

	ll query1(ll a, ll b, ll k, ll l, ll r)
	{
		if (r <= a || b <= l)return INF;
		else if (a <= l && r <= b) return seg_mi[k];
		else return min(query1(a, b, k * 2 + 1, l, (l + r) / 2), query1(a, b, k * 2 + 2, (l + r) / 2, r));
	}

	ll query2(ll a, ll b, ll k, ll l, ll r)
	{
		if (r <= a || b <= l)return INF;
		else if (a <= l && r <= b) return seg_mx[k];
		else return max(query2(a, b, k * 2 + 1, l, (l + r) / 2), query2(a, b, k * 2 + 2, (l + r) / 2, r));
	}
};


int main()
{
	RMQ seg_mi_x,seg_mx_x,seg_mi_y,seg_mx_y;

	cin >> n >> d;
	st.resize(n);

	rep(i, n) {
		cin >> st[i].second.first >> st[i].second.second >> st[i].first;
		seg_mi_x.update(i,st[i].second.first);
		seg_mx_x.update(i,st[i].second.first);
		seg_mi_y.update(i, st[i].second.second);
		seg_mx_y.update(i, st[i].second.second);
	}

	sort(st.begin(), st.end());

	ll ans = 0;
	for (int left = 0, right = 1; ;) {
		if (abs(st[left].first - st[right].first) <= d) {
			ll x = seg_mx_x.query2(0,n,left,left,right)-
				seg_mi_x.query1(0,n,left,left,right);
			ll y = seg_mx_y.query2(0, n, left, left, right) -
				seg_mi_y.query1(0, n, left, left, right);
			ans = max(ans, x*y);
			right++;
		}
		else {
			left++;
		}
		if (left >= n || right >= n)break;
	}
	cout << ans << endl;
	return 0;
}

