		p[3] = Y[max(yy-1, (int)0)];
		chmax(ans, (p[2]-p[0])*(p[3]-p[1]));
		//printf("%lld %lld %lld\n", x, y, c);
	}
	printf("%lld\n", ans);
	return 0;
}

