  return l.d < r.d;
}

int main() {
  ll n, d, ans = 0;
  cin >> n >> d;
  vector<STAR> v(n);

  rep(i, 0, n) {
    int a, b, c;
    cin >> a >> b >> c;
    v[i].x = a;
    v[i].y = b;
    v[i].d = c;
  }

  sort(v.begin(), v.end(), asc);

  int hx = v[0].x, lx = v[0].x, hy = v[0].y, ly = v[0].y, ld = v[0].d, lp = 0;
  rep(i, 0, n - 1) {
