      hx = max(hx, v[i + 1].x);
      hy = max(hy, v[i + 1].y);
      lx = min(lx, v[i + 1].x);
      ly = min(ly, v[i + 1].y);
    }else {
      ans = max(ans, (ll)(abs(hx - lx) * abs(hy - ly)));
      rep(j, lp + 1, i + 1) {
        if(v[i + 1].d - v[j].d <= d) {
          lp = j;
