#define INF (1<<29)
typedef long long ll;

struct MinSegtree
{
  int n;
  int dat[400005];
  MinSegtree(int _n)
  {
    n = 1;
    while(n < _n)
      n *= 2;
    for(int i=0;i<n*21;i++)
      dat[i] = INF;
  }

  void update(int k, int a)
  {
    k += n - 1;
    dat[k] = a;
    while(k > 0){
      k = (k - 1) / 2;
      dat[k] = min(dat[k * 2 + 1], dat[k * 2 + 2]);
    }
  }

  int query(int a, int b, int k, int l, int r)
  {
    if(r <= a || b <= l) return INF;

    if(a <= l && r <= b) return dat[k];
    else{
      int vl = query(a, b, k*2+1, l, (l + r) / 2);
      int vr = query(a, b, k*2+2, (l + r) / 2, r);
      return min(vl, vr);
    }
  }

  int size()
  {
    return n;
  }
};

struct MaxSegtree
{
  int n;
  int dat[400005];
  MaxSegtree(int _n){
    n = 1;
    while(n < _n)
      n *= 2;
    for(int i=0;i<n*2-1;i++)
      dat[i] = -1;
  }

  void update(int k, int a)
  {
    k += n - 1;
    dat[k] = a;
    while(k > 0){
      k = (k - 1) / 2;
      dat[k] = max(dat[k * 2 + 1], dat[k * 2 + 2]);
    }
  }

  int query(int a, int b, int k, int l, int r)
  {
    if(r <= a || b <= l) return -1;

    if(a <= l && r <=b ) return dat[k];
    else{
      int vl = query(a, b, k * 2 + 1, l, (l + r) / 2);
      int vr = query(a, b, k * 2 + 2, (l + r) / 2, r);
      return max(vl, vr);
    }
  }

  int size()
  {
    return n;
  }
};

int main()
{
  int n, d;
  scanf("%d%d", &n, &d);
  MinSegtree minx(n);
  MinSegtree miny(n);
  MaxSegtree maxx(n);
  MaxSegtree maxy(n);

  vector<pair<int, pair<int, int> > >s(n);
  vector<int>br(n);
  for(int i=0;i<n;i++){
    int x, y, b;
    scanf("%d%d%d", &x, &y, &b);
    s[i] = make_pair(b, make_pair(x, y));
    br[i] = b;
  }
  sort(s.begin(), s.end());
  sort(br.begin(), br.end());


  for(int i=0;i<n;i++){
    minx.update(i, s[i].second.first);
    maxx.update(i, s[i].second.first);
    miny.update(i, s[i].second.second);
    maxy.update(i, s[i].second.second);
  }

  br.push_back(INF);
  ll res = 0;
  for(int i=0;i<n;i++){
    int pos = (int)(upper_bound(br.begin(), br.end(), br[i] + d) - br.begin());
    int w = maxx.query(i, pos, 0, 0, maxx.size()) - minx.query(i, pos, 0, 0, minx.size());
    int h = maxy.query(i, pos, 0, 0, maxy.size()) - miny.query(i, pos, 0, 0, miny.size());
    res = max(res, (ll)w * (ll)h);
  }

  printf("%d\n", res);
  return 0;
}

