int i, j, n, d, stars[MAX_N][3], ax[2 * N], bx[2 * N], ay[2 * N], by[2 * N];
int ans = 0;
vector<PS> vec;

void vdc(vector<PS> &vf, S s, int now){
	if(vf[now].Se.sx > s.sx)
		vf[now].Se.sx = s.sx;
	if(vf[now].Se.lx < s.lx)
		vf[now].Se.lx = s.lx;
	if(vf[now].Se.sy > s.sy)
		vf[now].Se.sy = s.sy;
	if(vf[now].Se.ly < s.ly)
		vf[now].Se.ly = s.ly;
}

