		S s = S(a, a, b, b);
		it ite = lower_bound(vec.begin(), vec.end(), MP(c, s));
		if(vec[ite - vec.begin() - 1].F != c){
			vec.insert(ite, MP(c, s));
		}
		else{
			vdc(vec, s, ite - vec.begin() - 1);
		}
	}
	init(vec.size(), ax);
	init(vec.size(), bx);
	init(vec.size(), ay);
	init(vec.size(), by);
