template <typename T>
struct SegTreeMin
{
  private:
    ll N = 1;
    vector<T> node;

  public:
    SegTreeMin(vector<T> vec)
    {
        ll size = vec.size();

        while (N < size)
        {
            N *= 2;
        }

        node.resize(2 * N - 1, iINF);

        REP(i, size)
        {
            node[i + N - 1] = vec[i];
        }

        for (int i = N - 2; 0 <= i; i--)
        {
            node[i] = min(node[2 * i + 1], node[2 * i + 2]);
        }
    }

    void update(int i, T value)
    {
        i += (N - 1);

        node[i] = value;

        while (i > 0)
        {
            i = (i - 1) / 2;

            node[i] = min(node[2 * i + 1], node[2 * i + 2]);
        }
    }

    T getMin(int a, int b, int i = 0, int l = 0, int r = -1)
    {
        if (r < 0)
        {
            r = N;
        }

        if (r <= a || b <= l)
        {
            return iINF;
        }

        if (a <= l && r <= b)
        {
            return node[i];
        }

        T vl = getMin(a, b, 2 * i + 1, l, (l + r) / 2);

        T vr = getMin(a, b, 2 * i + 2, (l + r) / 2, r);

        return min(vl, vr);
    }
};

template <typename T>
struct SegTreeMax
{
  private:
    ll N = 1;
    vector<T> node;

  public:
    SegTreeMax(vector<T> vec)
    {
        ll size = vec.size();

        while (N < size)
        {
            N *= 2;
        }

        node.resize(2 * N - 1, -iINF);

        REP(i, size)
        {
            node[i + N - 1] = vec[i];
        }

        for (int i = N - 2; 0 <= i; i--)
        {
            node[i] = max(node[2 * i + 1], node[2 * i + 2]);
        }
    }

    void update(int i, T value)
    {
        i += (N - 1);

        node[i] = value;

        while (i > 0)
        {
            i = (i - 1) / 2;

            node[i] = max(node[2 * i + 1], node[2 * i + 2]);
        }
    }

    T getMax(int a, int b, int i = 0, int l = 0, int r = -1)
    {
        if (r < 0)
        {
            r = N;
        }

        if (r <= a || b <= l)
        {
            return -iINF;
        }

        if (a <= l && r <= b)
        {
            return node[i];
        }

        T vl = getMax(a, b, 2 * i + 1, l, (l + r) / 2);

        T vr = getMax(a, b, 2 * i + 2, (l + r) / 2, r);

        return max(vl, vr);
    }
};

int main()
{
    cin.tie(0);
    ios::sync_with_stdio(false);

    ll N, d;
    cin >> N >> d;

    vector<tuple<ll, ll, ll>> vec(N);

    SegTreeMax<ll> xMax(vector<ll>(N, -iINF)), yMax(vector<ll>(N, -iINF));
    SegTreeMin<ll> xMin(vector<ll>(N, iINF)), yMin(vector<ll>(N, iINF));

    ll ans = 0;

    REP(i, N)
    {
        ll x, y, d;
        cin >> x >> y >> d;

        vec[i] = make_tuple(d, x, y);
    }

    sort(ALL(vec));

    REP(i, N)
    {
        ll x, y, d;
        tie(d, x, y) = vec[i];

        xMax.update(i, x);
        xMin.update(i, x);
        yMax.update(i, y);
        yMin.update(i, y);
    }

    REP(i, N)
    {
        ll start = get<0>(vec[i]);

        auto itr = upper_bound(vec.begin(), vec.end(), make_tuple(d + start, iINF, iINF));

        if (itr == vec.begin())
            continue;
        if (itr == vec.end())
            continue;

        ll now = itr - vec.begin();

        ll x = xMax.getMax(i, now), y = yMax.getMax(i, now);
        ll _x = xMin.getMin(i, now), _y = yMin.getMin(i, now);

        ans = max(ans, (x - _x) * (y - _y));
    }

    Cout(ans);

    return 0;
}

