struct segtree
{
  int tree[524288];
  int size;

  void reset(bool option = false)
  {
    size = 1;
    while(size < n){
      size <<= 1;
    }

    if(option){
      for(int i = 0; i < size * 2 - 1; i++){
	tree[i] = -INF;
      }
    }
  }

  void update(int where, int val)
  {
    int now = size - 1 + where;
    tree[now] = val;
    while(now > 0){
      now = (now - 1) / 2;
      tree[now] = max(tree[now * 2 + 1], tree[now * 2 + 2]);
    }
  }

  int top()
  {
    return tree[0];
  }
} xmin, xmax, ymin, ymax;

signed main(){
