        rep(i,0,tmp.size()){
            a=max(a,tmp[i].second);
            b=max(b,tmp[i].first);
            c=min(c,tmp[i].second);
            d=min(d,tmp[i].first);
        }
        v1[0].push_back(a);
        v1[1].push_back(b);
        v2[0].push_back(c);
        v2[1].push_back(d);
    }

    int ans=0,s=0;
