ll query(ll a, ll b, ll k, ll l, ll r, ll c[]) {
  if(r <= a || b <= l) return INT_MAX;
  if(a <= l && r <= b) return c[k];

  ll vl = query(a, b, k * 2 + 1, l, (l + r) / 2, c);
  ll vr = query(a, b, k * 2 + 2, (l + r) / 2, r, c);

  return min(vl, vr);
}

bool asc(Star &l, Star &r) {
