int xm = INF, xM, ym = INF, yM;
void calc(){
	sizes[si] = last;
	sizeRef[last] = si;
	xms.update(si, xm);
	xMs.update(si, -xM);
	yms.update(si, ym);
	yMs.update(si, -yM);
	xm = INF, xM = 0, ym = INF, yM = 0;
}
