struct NODE{
  int min_d;
  int max_d;
  int min_x;
  int min_y;
  int max_x;
  int max_y;

  NODE(){
    min_d = INF;
    max_d = 0;
    min_x = INF;
    max_x = 0;
    min_y = INF;
    max_y = 0;
  }

  NODE(int a,int b,int c,int d,int e,int f){
    min_d = a;
    max_d = b;
    min_x = c;
    max_x = d;
    min_y = e;
    max_y = f;
  }

  NODE operator+ (const NODE &B) const{
    int a = min(min_d,B.min_d);
    int b = max(max_d,B.max_d);
    int c = min(min_x,B.min_x);
    int d = max(max_x,B.max_x);
    int e = min(min_y,B.min_y);
    int f = max(max_y,B.max_y);

    return NODE(a,b,c,d,e,f);
  }
};

NODE data[1 << 19];
int segn2;

NODE query(int a,int b,int k=0){
  if(b<a) return NODE();
  if(data[k].max_d < data[k].min_d) return NODE();
  if(data[k].max_d < a || b < data[k].min_d) return NODE();

  if(a<= data[k].min_d && data[k].max_d <= b) return data[k];

  return query(a,b,k*2+1) + query(a,b,k*2+2);
}

