#define Seg_Max_N (1<<18) 

using Value = pair<long long int,long long int>;
const Value ini = (make_pair(1e18, 0));
struct segtree {
	int N;
	vector<int>nums;
	vector<Value>dat;

	segtree() {}
	segtree(int n) :dat(2 * Seg_Max_N) ,nums(2*Seg_Max_N) {
		N = 1;
		while (N < n) N *= 2;
		for (int i = 0; i < 2 * N - 1; i++) {
			dat[i] = ini;
		}
	}
	// update k th element
	void update(int k, Value a) {
		k += N - 1; // leaf
		if (a == ini) {
			nums[k]--;
			if (!nums[k])dat[k] = a;
		}
		else {
			nums[k]++;
			dat[k] = a;
		}
		while (k > 0) {
			k = (k - 1) / 2;
			const Value al(dat[k * 2 + 1]);
			const Value ar(dat[k * 2 + 2]);
			dat[k] = make_pair(min(al.first,ar.first), max(al.second,ar.second));
		}
	}
	// min [a, b)
	Value  query(int a, int b) { return query(a, b, 0, 0, N); }
	Value  query(int a, int b, int k, int l, int r) {
		if (r <= a or b <= l) return ini;
		if (a <= l and r <= b) return dat[k];
		const int m = (l + r) / 2;
		const Value al(query(a, b, k * 2 + 1, l, m));
		const Value ar(query(a, b, k * 2 + 2, m, r));
		return make_pair(min(al.first, ar.first), max(al.second, ar.second));
	}
};
