		stars.push_back(star{ x,y,b });
		xs.push_back(x);
		ys.push_back(y);
	}
	Compress cpx(xs), cpy(ys);
	sort(stars.begin(), stars.end(), [](const star&l, const star&r) {
		return l.light < r.light;
	});
	for (auto &sta : stars) {
		sta.x = cpx.mp[sta.x];
		sta.y = cpy.mp[sta.y];
	}
	int l = 0;
	int r = 0;
	long long int ans = 0;
	{
		segx.update(stars[0].x, make_pair(stars[0].x, stars[0].x));
		segy.update(stars[0].y, make_pair(stars[0].y, stars[0].y));
		auto px(segx.query(0, amax));
		auto py(segy.query(0, amax));
		ans = max(ans, (px.second - px.first)*(py.second - py.first));
	}
	while (r<N-1) {
		if (stars[r+1].light <= stars[l].light+D) {
			r++;
			segx.update(stars[r].x, make_pair(stars[r].x, stars[r].x));
			segy.update(stars[r].y, make_pair(stars[r].y, stars[r].y));
			auto px(segx.query(0, amax));
			auto py(segy.query(0, amax));
			long long int disx = cpx.revmp[px.second] - cpx.revmp[px.first];
			long long int disy = cpy.revmp[py.second] - cpy.revmp[py.first];
			ans = max(ans, disx*disy);
		}
		else {
			if (l != r) {
				segx.update(stars[l].x, ini);
				segy.update(stars[l].y,ini);
				l++;
			}
			else {
				r++;
				segx.update(stars[r].x, make_pair(stars[r].x, stars[r].x));
				segy.update(stars[r].y, make_pair(stars[r].y, stars[r].y));
				segx.update(stars[l].x, ini);
				segy.update(stars[l].y, ini);
				l++;
				auto px(segx.query(0, amax));
				auto py(segy.query(0, amax));
				ans = max(ans, (px.second - px.first)*(py.second - py.first));
			}
		}
	}
	cout << ans << endl;
	return 0;
}

