    N = n_;
    limit = 1;
    while(limit<n_)limit*=2;
    dat.clear();
    dat.resize(2*limit,-LLINF);
  }

  ll _build(int cur,int L,int R,const vector<ll> &buf){
    if( !( 0 <= cur && cur < 2*limit ) ) return -LLINF;
    if( L == R-1 ) {
      if( L >= N ) return -LLINF;
      dat[cur] = buf[L];
    } else {
      ll vl = _build(cur*2+1,L,(L+R)/2,buf);
      ll vr = _build(cur*2+2,(L+R)/2,R,buf);
      dat[cur] = max(vl,vr);
    }
    return dat[cur];
  }

  void build(const vector<ll> &buf){ _build(0,0,limit,buf); };

  ll _query(int a,int b,int k,int l,int r){
    if(r<=a || b<=l)return -LLINF;
    else if(a<=l && r<=b)return dat[k];
    ll vl = _query(a,b,k*2+1,l,(l+r)/2);
    ll vr = _query(a,b,k*2+2,(l+r)/2,r);
    return max(vl,vr);
  }

  ll query(int a,int b){ return _query(a,b,0,0,limit); }
};

