class Sum_bit {
	std::vector<int> _array;
public:
	explicit Sum_bit(const int size) :_array(size, 0) {};
	void increment(int position, const int value) {
		while (position < _array.size()) {
			_array[position] += value;
			position += ~position & (position + 1);
		}
	}
	int operator[](int index) const {
		auto result = 0;
		while (index >= 0) {
			result += _array[index];
			index -= ~index & (index + 1);
		}
		return result;
	}
};
int find_first(int from, int to, const Sum_bit &bit, const int target) {
	while (from < to) {
		auto mid = (from + to) / 2;
		if (bit[mid] < target) {
			from = mid + 1;
		}
		else {
			to = mid;
		}
	}
	return from;
}
