	for (auto &star : stars) std::cin >> star.x >> star.y >> star.bright;
	std::sort(stars.begin(), stars.end(), [](const Star &left, const Star &right) {return left.bright < right.bright; });
	Sum_bit xBit(2000001), yBit(2000001);
	std::queue<Star> queue;
	long long int max = 0;
	for (const auto star : stars) {
		while (!queue.empty() && queue.front().bright + d < star.bright) {
			xBit.increment(queue.front().x, -1);
			yBit.increment(queue.front().y, -1);
			queue.pop();
		}
		queue.push(star);
		xBit.increment(star.x, 1);
		yBit.increment(star.y, 1);
		long long int xMin = find_first(0, 2000000, xBit, 1);
		auto xMax = find_first(0, 2000000, xBit, queue.size());
		auto yMin = find_first(0, 2000000, yBit, 1);
		auto yMax = find_first(0, 2000000, yBit, queue.size());
		if (max < (xMax - xMin) * (yMax - yMin)) {
			max = (xMax - xMin) * (yMax - yMin);
		}
	}
	std::cout << max << std::endl;
}

