class MinSegment {
	std::vector<std::vector<int>> mArray;
public:
	MinSegment(const std::vector<int> &init) :mArray(std::ceil(std::log2(init.size()) + 1), std::vector<int>()) {
		mArray[0] = init;
		auto size = init.size();
		for (auto i = 1; i < mArray.size(); ++i) {
			size = (size + 1) >> 1;
			mArray[i] = std::vector<int>(size, INT_MAX);
			for (auto j = 0; j < mArray[i - 1].size(); ++j) {
				if (mArray[i][j >> 1] > mArray[i - 1][j]) mArray[i][j >> 1] = mArray[i - 1][j];
			}
		}
	}
	long long int min(int from, int to) const {
		int res = INT_MAX;
		for (auto i = 0; i < mArray.size() && from <= to; ++i) {
			if (from % 2 == 1 && res > mArray[i][from]) {
				res = mArray[i][from];
			}
			if (to % 2 == 0 && res > mArray[i][to]) {
				res = mArray[i][to];
			}
			from = (from + 1) >> 1;
			to = (to - 1) >> 1;
		}
		return res;
	}
};
class MaxSegment {
	std::vector<std::vector<int>> mArray;
public:
	MaxSegment(const std::vector<int> &init) :mArray(std::ceil(std::log2(init.size()) + 1), std::vector<int>()) {
		mArray[0] = init;
		auto size = init.size();
		for (auto i = 1; i < mArray.size(); ++i) {
			size = (size + 1) >> 1;
			mArray[i] = std::vector<int>(size, INT_MIN);
			for (auto j = 0; j < mArray[i - 1].size(); ++j) {
				if (mArray[i][j >> 1] < mArray[i - 1][j]) mArray[i][j >> 1] = mArray[i - 1][j];
			}
		}
	}
	long long int max(int from, int to) const {
		int res = INT_MIN;
		for (auto i = 0; i < mArray.size() && from <= to; ++i) {
			if (from % 2 == 1 && res < mArray[i][from]) {
				res = mArray[i][from];
			}
			if (to % 2 == 0 && res < mArray[i][to]) {
				res = mArray[i][to];
			}
			from = (from + 1) >> 1;
			to = (to - 1) >> 1;
		}
		return res;
	}
};
struct Star {
	int x, y, brightness;
};
int main() {
	int n, d;
	std::cin >> n >> d;
	std::vector<Star> stars(n);
	for (auto &star : stars) std::cin >> star.x >> star.y >> star.brightness;
	std::sort(stars.begin(), stars.end(), [](const Star a, const Star b) {return a.brightness < b.brightness; });
	std::vector<int> b(n);
	std::vector<int> x(n);
	std::vector<int> y(n);
	for (auto i = 0; i < n; ++i) {
		b[i] = stars[i].brightness;
		x[i] = stars[i].x;
		y[i] = stars[i].y;
	}
	MaxSegment maxX(x);
	MinSegment minX(x);
	MaxSegment maxY(y);
	MinSegment minY(y);
	long long int max = 0;
	for (auto left = 0; left < n; ++left) {
		auto right = std::upper_bound(b.begin() + left, b.end(), b[left] + d) - 1 - b.begin();
