	return a.first < b.first;
}
int last = -1, si = -1;
int xm = INF, xM, ym = INF, yM;
void calc(){
	sizes[si] = last;
//	sizeRef[last] = si;
	xms.update(si, xm);
	xMs.update(si, -xM);
	yms.update(si, ym);
	yMs.update(si, -yM);
	xm = INF, xM = 0, ym = INF, yM = 0;
}
// O(N log N)
int main() {
	omajinai;
	cin >> n >> d;

	// segment tree initial
	int m=1;
	while(m<n)m*=2;
	xms.init(m);
	xMs.init(m);
	yms.init(m);
	yMs.init(m);
	//

	REP(i, n){
		int x, y, b;
		cin >> x >> y >> b;
		stars[i] = MP(b, MP(x, y));
	}
	sort(stars, stars + n, comp);
	REP(i, n){
		int b = stars[i].first, x = stars[i].second.first, y = stars[i].second.second;
		if(b!=last){
			if(si > -1)calc();
			si++;
			last = b;
		}
		xm = min(xm, x);
		xM = max(xM, x);
		ym = min(ym, y);
		yM = max(yM, y);
	}
	calc();

	si++; // to use as sizes' size
	ll ans = 0; // max = 2e6 ^ 2 = 4e12
	REP(i, si){
		int ed = upper_bound(sizes, sizes + si, sizes[i] + d) - sizes;
		xm = xms.query(i, ed);
		xM = -xMs.query(i, ed);
		ym = yms.query(i, ed);
		yM = -yMs.query(i, ed);
		ans = max(ans, (ll)(xM - xm) * (yM - ym));
		if(ed == si)break;
	}
	cout << ans << endl;
}

