template <typename T>
struct RangeMinimumQuery {
	vector<T> value;
	int size;

	RangeMinimumQuery(int N, T x = numeric_limits<T>::max()){
		for (size = 1; size < N; size *= 2){}
		value.assign(size * 2 - 1, x);
	}

	void update(int k, int x){
		k += size - 1;
		value[k] = x;
		while (k > 0){
			k = (k - 1) / 2;
			value[k] = min(value[k * 2 + 1], value[k * 2 + 2]);
		}
	}

	T _getMin(int l, int r, int k, int a, int b){
		if (b <= l || r <= a) return numeric_limits<T>::max();
		if (l <= a && b <= r) return value[k];
		int m = (a + b) / 2;
		T vl = _getMin(l, r, k * 2 + 1, a, m);
		T vr = _getMin(l, r, k * 2 + 2, m, b);
		return min(vl, vr);
	}

	T getMin(int l, int r){
		return _getMin(l, r, 0, 0, size);
	}
};

