		segry.update(i, -get<2>(star[i]));
	}

	lint res = 0;
	for (int left = 0, right = 0; left < n; left++){
		while (true){
			int ld = get<0>(star[left]);
			int rd = get<0>(star[right]);
			if (rd - ld > d) break;
			right++;
		}

		int width = -segrx.getMin(left, right) - seglx.getMin(left, right);
		int height = -segry.getMin(left, right) - segly.getMin(left, right);
		lint area = (lint)width * height;
		res = max(res, area);
	}

	printf("%lld\n", res);

	return 0;
}

