struct segtree
{
  int tree[524288];
  int size;

  void reset()
  {
    size = 1;
    while(size < n){
      size <<= 1;
    }
  }

  void update(int where, int val)
  {
    int now = size - 1 + where;
    while(true){
      tree[now] = max(val, now);
      if(now == 0){
	return;
      }
      now = (now - 1) / 2;
    }
  }

  int top()
  {
    return tree[0];
  }
} xmin, xmax, ymin, ymax;

signed main(){
  scanf("%lld%lld", &n, &d);
