    scanf("%lld%lld%lld", &x[i], &y[i], &b[i]);
  }

  for(int i = 0; i < n; i++){
    sorted[i] = MP(b[i], i);
  }

  sort(sorted, sorted + n);

  xmin.reset(), xmax.reset(), ymin.reset(), ymax.reset();

  int start = 0, nowd = 0;
  for(int i = 0; i < n; i++){
    nowd = sorted[i].first;
    xmin.update(i, -x[sorted[i].second]);
    xmax.update(i, x[sorted[i].second]);
