using namespace std;

typedef pair<int, int> P;
typedef long long ll;

P tx[1 << 19];
P ty[1 << 19];

P update(P* t, int i, int x, int k, int l, int r){
	if(i < l || r <= i){
		return t[k];
	}
	if(i <= l && r <= i + 1){
		return t[k] = P(x, x);
	}
	int m = (l + r) / 2;
	P p = update(t, i, x, 2 * k + 1, l, m);
	P q = update(t, i, x, 2 * k + 2, m, r);
	return t[k] = P(
		min(p.first, q.first),
		max(p.second, q.second)
	);
}
void update(P* t, int i, int x){
	update(t, i, x, 0, 0, 1 << 18);
}

P query(P* t, int a, int b, int k, int l, int r){
	if(b <= l || r <= a){
		return P(1e7, 0);
	}
	if(a <= l && r <= b){
		return t[k];
	}
	int m = (l + r) / 2;
	P p = query(t, a, b, 2 * k + 1, l, m);
	P q = query(t, a, b, 2 * k + 2, m ,r);
	return P(
		min(p.first, q.first),
		max(p.second, q.second)
	);
}
P query(P* t, int a, int b){
	return query(t, a, b, 0, 0, 1 << 18);
}

