	}
	ll ans = 0;
	int s = 0, t = 0;
	while(s <  n){
		while(t < n && b[t].first <= b[s].first + d){
			++t;
		}
		P p = query(tx, s, t);
		P q = query(ty, s, t);
		ll dx = p.second - p.first;
		ll dy = q.second - q.first;
