ll min(ll a, ll b) {
  return a < b ? a : b;
}

ll max(ll a, ll b) {
  return a > b ? a : b;
}

int main(void) {
  ll i, j, k, n, d, sn = 1, now = 1, ans = 0;
  scanf("%lld%lld", &n, &d);
  pair< ll, ll > sx[n], sy[n];
  ll b[n];
  for(i = 0; i < n; ++i) {
    scanf("%lld%lld%lld", &sx[i].second, &sy[i].second, &b[i]);
    sy[i].first = sx[i].first = b[i];
  }
  sort(sx, sx + n), sort(sy, sy + n), sort(b, b + n);
  while(sn < n) sn *= 2;
  ll xmin[sn * 2], xmax[sn * 2], ymin[sn * 2], ymax[sn * 2], las[sn * 2];
  for(i = 1; i < sn * 2; ++i) {
    if(i >= now * 2) now *= 2;
    las[i] = sn / now * (i - now + 1) - 1;
  }
  for(i = 0; i < sn; ++i) {
    if(i < n) xmin[i + sn] = sx[i].second, xmax[i + sn] = sx[i].second, ymin[i + sn] = sy[i].second, ymax[i + sn] = sy[i].second;
    else xmin[i + sn] = ymin[i + sn] = 1e9, xmax[i + sn] = ymax[i + sn] = -1;
    if(i % 2) {
      now = (i + sn) / 2;
      while(now) {
        xmin[now] = min(xmin[now * 2], xmin[now * 2 + 1]);
        xmax[now] = max(xmax[now * 2], xmax[now * 2 + 1]);
        ymin[now] = min(ymin[now * 2], ymin[now * 2 + 1]);
        ymax[now] = max(ymax[now * 2], ymax[now * 2 + 1]);
        now /= 2;
      }
    }
  }
  for(i = 0; i < n; ++i) {
    ll l = lower_bound(b, b + n, b[i] + d + 1) - b - 1;
    ll nxmi, nxma, nymi, nyma;
    nxmi = nymi = 1e9, nxma = nyma = -1;
    now = i + sn;
    while(1) {
      while(!(now % 2)) now /= 2;
      while(las[now] > l) now *= 2;
      if(nxmi > xmin[now]) nxmi = xmin[now];
      if(nxma < xmax[now]) nxma = xmax[now];
      if(nymi > ymin[now]) nymi = ymin[now];
      if(nyma < ymax[now]) nyma = ymax[now];
      if(las[now] == l) break;
      now++;
    }
    if(nxma - nxmi >= 0 && nyma - nymi >= 0 && ans < (nxma - nxmi) * (nyma - nymi)) ans = (nxma - nxmi) * (nyma - nymi);
  }
  printf("%lld\n", ans);
  return 0;
}

